       ApplicationImplementationFactory = new UIAutomationOutOfProcessApplicationFactory()
   });
 
   a.Start();
   a.WaitForMainWindow(TimeSpan.FromMilliseconds(5000));
   
   // Perform various tests...
   
   a.Close();
}

